import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';  // Import the App.css file

function App() {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const apiKey = 'Y'; // Replace with your WeatherAPI key
  const apiUrl = `https://api.weatherapi.com/v1/current.json?key=${apiKey}&q=${encodeURIComponent(city)}&aqi=no`;

  const fetchWeather = async () => {
    if (city.trim() === '') return;  // Don't make request if city is empty
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(apiUrl);
      console.log(response.data);  // Log the API response for debugging
      setWeatherData(response.data);
    } catch (err) {
      console.error(err);  // Log the error for debugging
      setError('City not found or API request failed');
    }
    setLoading(false);
  };

  const handleCityChange = (event) => {
    setCity(event.target.value);
  };

  useEffect(() => {
    if (city) {
      fetchWeather();
    }
  }, [city]);

  return (
    <div className="App">
      <h1>Weather App</h1>
      <input
        type="text"
        value={city}
        onChange={handleCityChange}
        placeholder="Enter city"
      />
      <button onClick={fetchWeather}>Get Weather</button>

      {loading && <p>Loading...</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {weatherData && (
        <div>
          <h2>{weatherData.location.name}</h2>
          <p>Temperature: {weatherData.current.temp_c}Â°C</p>
          <p>Weather: {weatherData.current.condition.text}</p>
          <p>Humidity: {weatherData.current.humidity}%</p>
          <p>Wind Speed: {weatherData.current.wind_kph} kph</p>
        </div>
      )}
    </div>
  );
}

export default App;
